export * from './base' ;
export * from './map' ;
export * from './reduce' ;

/**
 * Algorithm used by {@link product} to compute the product of one or more
 * iterables from pools of symbols.
 *
 * @example
 * // Ax Ay Bx By Cx Cy Dx Dy
 * _product(['xy', 'ABCD'], 0 , 2) ;
 *
 * @example
 * // 000 001 010 011 100 101 110 111
 * _product([[0,1],[0,1],[0,1]], 0 , 3) ;
 *
 * @param {Array[]} pools - The pools of symbols in reverse order.
 * @param {Number} i - Index of the pool to draw the next symbol from
 * @param {Number} n - Number of pools in total.
 * @returns {Iterator}
 */
export function* _product ( pools , i , n ) {

	if ( i === n ) { yield [ ] ; return ; }

	let iterable = pools[i] ;

	for ( let buffer of _product( pools , i + 1 , n ) ) {

		for ( let item of iterable ) {

			buffer.push( item ) ;

			yield buffer ;

			buffer.pop( item ) ;

		}

	}

}

import { list , map , iter , _next } from '..' ;

/**
 * Zips iterables together. Yields a tuple containing the first element of each
 * iterable, then a tupe containing the second element of each iterable, etc.
 * Stops when one of the iterables runs out of elements.
 *
 * @example
 * // returns [ [ 'a' , 1 ] , [ 'b' , 2 ] , [ 'c' , 3 ] ]
 * list( _zip( [ 'abcd' , range(3) ] ) ) ;
 *
 * @param {Iterable[]} iterables - The iterables to zip.
 * @returns {Iterator}
 *
 */
export function* _zip ( iterables ) {

	let iterators = list( map( iter , iterables ) ) ;

	if ( iterators.length === 0 ) return ;

	while ( true ) {

		let buffer = [ ] ;

		for ( let result of map( _next , iterators ) ) {

			if ( result.done ) return ;

			buffer.push( result.value ) ;

		}

		yield buffer ;

	}

}

import { iter } from '..' ;

/**
 * Zips exactly two iterables together. Yields a tuple containing the first
 * element of each iterable, then a tupe containing the second element of each
 * iterable, etc.  Stops when one of the two iterables runs out of elements.
 *
 * @example
 * // returns [ [ 'a' , 1 ] , [ 'b' , 2 ] , [ 'c' , 3 ] ]
 * list( _zip2( 'abcd' , range(3) ) ) ;
 *
 * @param {Iterable} A - The first iterable.
 * @param {Iterable} B - The second iterable.
 * @returns {Iterator}
 *
 */
export function* _zip2 ( A , B ) {

	A = iter( A ) ;
	B = iter( B ) ;

	while ( true ) {

		const a = A.next() ;
		if ( a.done ) return ;

		const b = B.next() ;
		if ( b.done ) return ;

		yield [ a.value , b.value ] ;

	}

}

import { list , map , iter , _next , repeat , enumerate } from '..' ;

/**
 * Same as _zip, but continues to yield zipped tuples until the last iterable is
 * exhausted.
 *
 * @example
 * // returns [['A','x'],['B','y'],['C','-'],['D','-']]
 * list( _ziplongest( '-' , [ 'ABCD', 'xy' ] ) ) ;
 *
 * @param fillvalue - The value to yield for iterators that are exhausted.
 * @param {Iterable[]} iterables - The iterables to zip.
 * @returns {Iterator}
 *
 */
export function* _ziplongest ( fillvalue, iterables ) {

    let counter = iterables.length ;

    if ( counter === 0 ) return ;

    const filler = repeat( fillvalue ) ;

    const iterators = list( map( iter , iterables ) ) ;

    while ( true ) {

	const buffer = [ ] ;

	for ( const [ i , e ] of enumerate( map( _next , iterators ) ) ) {
	    if ( e.done ) {
		--counter ;
		if ( counter === 0 ) return ;
		iterators[i] = filler ;
		buffer.push( fillvalue ) ;
	    }
	    else {
		buffer.push( e.value ) ;
	    }
	}

	yield buffer ;

    }

}

/**
 * Chains input iterables one after the other. Returns an iterator that yields
 * all values of the first input iterable, then all values of the second, etc.
 *
 * @example
 * // returns [ 0 , 1 , 0 , 1 , 2 ]
 * list( chain( [ range( 2 ) , range( 3 ) ] ) ) ;
 *
 * @param {Iterable[]} iterables - The input iterables to chain.
 * @returns {Iterator}
 *
 */
export function* chain ( iterables ) {

	for ( let iterable of iterables ) yield* iterable ;

}

import { list , range , pick } from '..' ;

/**
 * Yields all combinations of each possible choice of <code>r</code> elements
 * of the input iterable.
 *
 * @example
 * // AB AC AD BC BD CD
 * combinations('ABCD', 2)
 *
 * @example
 * // 012 013 023 123
 * combinations(range(4), 3)
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} r - The size of the combinations to generate.
 * @returns {Iterator}
 */
export function* combinations ( iterable , r ) {

	let pool = list( iterable ) ;
	let len = pool.length ;

	if ( r > len ) return ;

	let indices = list( range( 0 , r , 1 ) ) ;

	yield list( pick( pool , indices ) ) ;

	while ( true ) {

		let i = r - 1 ;

		while ( true ) {

			if ( i < 0 ) return ;

			if ( indices[i] !== i + len - r ) {

				let pivot = ++indices[i] ;

				for ( ++i ; i < r ; ++i ) indices[i] = ++pivot ;

				break ;

			}

			--i ;

		}

		yield list( pick( pool , indices ) ) ;

	}

}

import { list , nrepeat , pick } from '..' ;

/**
 * Yields all combinations, with repetitions, of each possible choice of
 * <code>r</code> elements of the input iterable.
 *
 * @example
 * // A B C
 * combinationswithrepetition('ABC', 1)
 *
 * @example
 * // 00 01 02 11 12 22
 * combinationswithrepetition(range(3), 2)
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} r - The size of the combinations to generate.
 * @returns {Iterator}
 */

export function* combinationswithrepetition ( iterable , r ) {

	let pool = list( iterable ) ;
	let len = pool.length ;

	if ( len === 0 && r > 0 ) return ;

	let indices = list( nrepeat( 0 , r ) ) ;

	yield list( pick( pool , indices ) ) ;

	while ( true ) {

		let i = r - 1 ;

		while ( true ) {

			if ( i < 0 ) return ;

			if ( indices[i] !== len - 1 ) {

				let pivot = ++indices[i] ;

				for ( ++i ; i < r ; ++i ) indices[i] = pivot ;

				break ;

			}

			--i ;

		}

		yield list( pick( pool , indices ) ) ;

	}

}

import { _zip2 } from '..' ;

/**
 * Filters the first input iterable according to truthy and flasy values from a
 * second input iterable. The ith element of the first input iterable is output
 * if and only if the ith element of the second input iterable is truthy.
 *
 * @param {Iterable} iterable - The first input iterable to filter.
 * @param {Iterable} selector - The second input iterable containing the truthy and falsy
 * values.
 * @returns {Iterator}
 */
export function* compress ( iterable , selector ) {

	for ( let [ take , item ] of _zip2( selector , iterable ) ) {

		if ( take ) yield item ;

	}

}

import { iter } from '..' ;

/**
 * Drop elements of the input iterable while the current element satisfies the
 * input predicate.
 *
 * @param {Function} predicate - The input predicate.
 * @param {Iterable} iterable - The input iterable.
 * @returns {Iterator}
 */
export function* dropwhile ( predicate , iterable ) {

	let iterator = iter( iterable ) ;

	for ( let item of iterator ) {

		if ( predicate( item ) ) continue ;

		// equivalent to return chain( [ [ item ] , iterator ] ) ;

		yield item ;

		yield* iterator ;

		return ;

	}

}

import { _zip2 , _count } from '..' ;

/**
 * Yields (index,element) tuples where the elements are taken from the input
 * iterable. You can choose the starting index. The starting index is
 * <code>0</code> by default.
 *
 * @example
 * // returns [ [ 0 , 'a' ] , [ 1 , 'b' ] , [ 2 , 'c' ] ]
 * list( enumerate( 'abc' ) ) ;
 *
 * @example
 * // returns [ [ 1 , 'a' ] , [ 2 , 'b' ] , [ 3 , 'c' ] ]
 * list( enumerate( 'abc' , 1 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} [start=0] - The starting index.
 * @returns {Iterator}
 */
export function enumerate ( iterable, start = 0 ) {

	return _zip2( _count( start , 1 ) , iterable ) ;

}

/**
 * Yields all elements from input iterable that verify a given predicate.
 *
 * @example
 * // returns [ 1 , 3 , 5 , 7 , 9 ]
 * list( filter( x => x % 2 , range( 10 ) ) ) ;
 *
 * @param {Function} predicate - A unary function that returns a truthy or
 * falsy value.
 * @param {Iterable} iterable - The input iterable.
 * @returns {Iterator}
 *
 */
export function* filter ( predicate , iterable ) {

	for ( let item of iterable ) {

		if ( predicate( item ) ) yield item ;

	}

}

/**
 * Same as {@link filter}.
 * @function filtertrue
 */
export const filtertrue = filter ;

/**
 * Yields all elements from input iterable that do not verify a given predicate.
 *
 * @example
 * // returns [ 0 , 2 , 4 , 6 , 8 ]
 * list( filter( x => x % 2 , range( 10 ) ) ) ;
 *
 * @param {Function} predicate - A unary function that returns a truthy or
 * falsy value.
 * @param {Iterable} iterable - The input iterable.
 * @returns {Iterator}
 *
 */
export function* filterfalse ( predicate , iterable ) {

	for ( let item of iterable ) {

		if ( ! predicate( item ) ) yield item ;

	}

}

import { iter } from '..' ;

/**
 * Yields elements of the input iterable by grouping them into tuples
 * consecutive elements from the same equivalence class.
 *
 * @example
 * // A B C D A B
 * list( map( ( k , g ) => k , groupby('AAAABBBCCDAABBB') ) )
 *
 * @example
 * // AAAA BBB CC D
 * list( map( ( k , g ) => list( g ) , groupby('AAAABBBCCD') ) )
 *
 * @param {Function} key - The function used to determine the equivalence class
 * of an element.
 * @param {Iterable} iterable - The input iterable.
 * @returns {Iterator}
 */
export function* group ( key , iterable ) {

	let iterator = iter( iterable ) ;

	let first = iterator.next() ;

	if ( first.done ) return ;

